import zipfile
import re
from typing import List

pptx_path = "/Users/charles/spec_tool_call/gaia_dataset/level1/example_024/a3fbeb63-0e8c-4a11-bff6-0e3b484c3e9c.pptx"


def count_with_python_pptx(path: str):
    try:
        from pptx import Presentation  # type: ignore
    except Exception:
        return None
    try:
        prs = Presentation(path)
        count = 0
        for slide in prs.slides:
            texts = []
            for shape in slide.shapes:
                # text frames
                if hasattr(shape, "has_text_frame") and shape.has_text_frame:
                    try:
                        texts.append(shape.text)
                    except Exception:
                        # fallback: aggregate paragraphs
                        try:
                            buf = []
                            for p in shape.text_frame.paragraphs:
                                buf.append("".join(r.text or "" for r in p.runs))
                            texts.append("\n".join(buf))
                        except Exception:
                            pass
                # tables
                if hasattr(shape, "has_table") and shape.has_table:
                    try:
                        for row in shape.table.rows:
                            for cell in row.cells:
                                texts.append(cell.text)
                    except Exception:
                        pass
            joined = "\n".join(t for t in texts if t)
            if "crustacean" in joined.lower():
                count += 1
        return count
    except Exception:
        return None


def count_with_zip_xml(path: str):
    # parse slide XMLs for text runs (a:t)
    ns_a = "{http://schemas.openxmlformats.org/drawingml/2006/main}"
    slide_prefix = "ppt/slides/"
    slide_files: List[str] = []
    with zipfile.ZipFile(path, 'r') as zf:
        for name in zf.namelist():
            if name.startswith(slide_prefix) and name.endswith('.xml'):
                slide_files.append(name)
        slide_files.sort()
        count = 0
        import xml.etree.ElementTree as ET
        for s in slide_files:
            try:
                data = zf.read(s)
                root = ET.fromstring(data)
                texts = []
                for t in root.iter(ns_a + 't'):
                    if t.text:
                        texts.append(t.text)
                # also consider paragraph fields a:fld/a:t in case
                joined = " ".join(texts).lower()
                if "crustacean" in joined:
                    count += 1
            except Exception:
                # if any slide fails, skip
                pass
    return count


count = count_with_python_pptx(pptx_path)
if count is None:
    count = count_with_zip_xml(pptx_path)

print(count)

import zipfile, re, xml.etree.ElementTree as ET

path = "/Users/charles/spec_tool_call/gaia_dataset/level1/example_024/a3fbeb63-0e8c-4a11-bff6-0e3b484c3e9c.pptx"

pattern = re.compile(r"\bcrustaceans?\b", re.IGNORECASE)

count = 0
try:
    with zipfile.ZipFile(path, 'r') as z:
        slide_files = sorted(
            [name for name in z.namelist() if name.startswith('ppt/slides/slide') and name.endswith('.xml')]
        )
        for s in slide_files:
            xml_bytes = z.read(s)
            try:
                root = ET.fromstring(xml_bytes)
            except ET.ParseError:
                continue
            texts = []
            for el in root.iter():
                if isinstance(el.tag, str) and el.tag.endswith('}t'):
                    if el.text:
                        texts.append(el.text)
            full_text = ' '.join(texts)
            if pattern.search(full_text):
                count += 1
except FileNotFoundError:
    print("FILE_NOT_FOUND")
    raise

print(count)
