# Gather values and colors
rows = ws.max_row
cols = ws.max_column

def color_hex(cell):
    fill = cell.fill
    if fill is None or fill.fgColor is None:
        return None
    fg = fill.fgColor
    # openpyxl can give type 'rgb' or theme/indexed.
    if fg.type == 'rgb' and fg.rgb:
        rgb = fg.rgb
        # Sometimes alpha included: ARGB
        if len(rgb) == 8:
            rgb = rgb[2:]  # drop alpha? Actually ARGB-> drop first two
            # But ARGB is usually 8 hex, first two are alpha.
            # Keep last 6
        return rgb.upper()
    # Try start_color from fill
    try:
        sc = fill.start_color
        if sc and sc.rgb:
            rgb = sc.rgb
            if len(rgb) == 8:
                rgb = rgb[2:]
            return rgb.upper()
    except Exception:
        pass
    return None

grid = []
start = None
end = None
for r in range(1, rows+1):
    row_data = []
    for c in range(1, cols+1):
        cell = ws.cell(row=r, column=c)
