from openpyxl import load_workbook
from pathlib import Path

path = Path('/Users/charles/spec_tool_call/gaia_dataset/level1/example_027/65afbc8a-89ca-4ad5-8d62-355bb401f61d.xlsx')
wb = load_workbook(path)
ws = wb.active

rows = ws.max_row
cols = ws.max_column

# Extract grid with value and 6-digit hex color (None if no solid fill)

def get_hex(cell):
    fill = cell.fill
    if not fill or fill.fill_type is None:
        return None
    # Try fgColor.rgb
    color = None
    if fill.fgColor is not None:
        if fill.fgColor.type == 'rgb' and fill.fgColor.rgb:
            rgb = fill.fgColor.rgb
            if rgb and len(rgb) == 8:  # ARGB
                return rgb[-6:].upper()
            elif rgb and len(rgb) == 6:
                return rgb.upper()
        # theme/indexed colors are trickier; ignore as None
    return None

grid = [[{'v': ws.cell(r, c).value, 'c': get_hex(ws.cell(r, c))} for c in range(1, cols+1)] for r in range(1, rows+1)]

# Find START and END
start = None
end = None
for r in range(rows):
    for c in range(cols):
        v = grid[r][c]['v']
        if v == 'START' and start is None:
            start = (r+1, c+1)
        if v == 'END' and end is None:
            end = (r+1, c+1)

# Fallbacks if END isn't labeled; use bottom-right if value provided externally
if start is None:
    start = (1,1)
if end is None:
    end = (rows, cols)

# Movement rules
BLUE = '0099FF'
# Moves are exactly two cells; cannot move backward (i.e., opposite of previous direction)
dirs = {
    'U': (-2, 0),
    'D': (2, 0),
    'L': (0, -2),
    'R': (0, 2),
}
opposite = {'U':'D', 'D':'U', 'L':'R', 'R':'L'}

# Helper to check if a landing cell is blue

def is_blue(r, c):
    if r < 1 or r > rows or c < 1 or c > cols:
        return True
    color = grid[r-1][c-1]['c']
    return color == BLUE

from collections import deque

# BFS state: (r, c, prev_dir)
q = deque()
q.append((start[0], start[1], None))
# For path reconstruction include parent pointers keyed by (r,c,prev_dir_index?)
# Because prev_dir matters for state transitions; but for path we need just positions sequence.
# We'll store visited with prev_dir to respect no-backtracking constraint.
visited = set()
visited.add((start[0], start[1], None))
parent = {}  # key: (r,c,prev_dir) -> (pr, pc, pprev_dir)

found = None
while q:
    r, c, pd = q.popleft()
    if (r, c) == end:
        found = (r, c, pd)
        break
    for dkey, (dr, dc) in dirs.items():
        if pd is not None and dkey == opposite[pd]:
            continue  # cannot move backward
        nr, nc = r + dr, c + dc
        # must land within grid and not on blue
        if 1 <= nr <= rows and 1 <= nc <= cols:
            if not is_blue(nr, nc):
                state = (nr, nc, dkey)
                if state not in visited:
                    visited.add(state)
                    parent[state] = (r, c, pd)
                    q.append(state)

# Reconstruct path positions (landing cells including start)
path = []
if found is not None:
    cur = found
    while cur in parent or (cur[0], cur[1]) == start:
        path.append((cur[0], cur[1]))
        if (cur[0], cur[1]) == start:
            break
        cur = parent[cur]
    path.reverse()

# path[0] is start. We need the cell after 11 moves => index 11 in path (0-based), if exists
result = None
if path and len(path) > 11:
    r11, c11 = path[11]
    color11 = grid[r11-1][c11-1]['c']
    result = {
        'rows': rows,
        'cols': cols,
        'start': start,
        'end': end,
        'path_length': len(path)-1,
        'turn11_cell': (r11, c11),
        'turn11_color': color11,
    }
else:
    result = {
        'rows': rows,
        'cols': cols,
        'start': start,
        'end': end,
        'path_available': found is not None,
        'path_length': (len(path)-1) if path else None,
        'note': 'Path too short for 11 turns' if path else 'No path found'
    }

print(result)
